mapper.select:
SELECT a.id_article, a.nom_article, a.content, a.description_robot_article, a.description_article FROM astanble.article AS a
JOIN astanble.article_thematique AS at ON at.id_article = a.id_article
WHERE at.id_thematique = #{id_thematique}::int;

mapper.update:
UPDATE astanble.article
SET nom_article = #{article.nom_article},
    content = #{article.content},
    description_article = #{article.description_article}
WHERE id_article = #{article.id_article}::int;
